import { useRef,useEffect } from "react"
import './map.css'
import { GoogleMap , useLoadScript, Marker} from "@react-google-maps/api"

const Map=({setShowMap, location})=>{
    const {isLoaded}= useLoadScript({
        googleMapsApiKey:process.env.NEXT_PUBLIC_PRIVATE_API_KEY
    })
    if(!isLoaded){
        return (
            <div>Loading ...</div>
        )
    }
    return (
        <GoogleMap zoom={18} center={location} mapContainerClassName='map'>
            <Marker />
        </GoogleMap>
    )
}
export default Map
