import { useState } from "react";
import {
  Button,
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Typography,
} from "@mui/material";
import InsertEmoticonIcon from "@mui/icons-material/InsertEmoticon";
import SentimentVeryDissatisfiedIcon from "@mui/icons-material/SentimentVeryDissatisfied";

const PickItem = ({ pick }) => {
  const { name, description, image, like, dislike } = pick;
  const [numberOfLike, setNumberOfLike] = useState(like);
  const [numberOfDislike, setNumberOfDislike] = useState(dislike);
  const likeHandler = () => {
    setNumberOfLike((pre) => pre + 1);
  };
  const dislikeHandler = () => {
    setNumberOfDislike((pre) => pre + 1);
  };

  return (
    <Card sx={{ maxWidth: 400 }}>
      <div style={{ display: "flex", justifyContent: "center" }}>
        <CardMedia component="img" height="250" image={image} alt={name} />
      </div>
      <CardContent>
        <Typography align="left" gutterBottom variant="h5" component="div">
          {name}
        </Typography>
        <Typography align="left" variant="body1" color="text.secondary">
          {description}
        </Typography>
      </CardContent>
      <CardActions>
        <Button id="happy-btn" size="small">
          <InsertEmoticonIcon />
        </Button>
        <Button id="sad-btn" size="small">
          <SentimentVeryDissatisfiedIcon />
        </Button>
      </CardActions>
    </Card>
  );
  return (
    <Card sx={{ maxWidth: 400  }}>
      <CardMedia
        sx={{ height: 250 }}
        image={image} 
        alt={name}
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {name}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {description}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small"><InsertEmoticonIcon />{numberOfLike}</Button>
        <Button size="small"><SentimentVeryDissatisfiedIcon />{numberOfDislike}</Button>
      </CardActions>
    </Card>
  );
};
export default PickItem;
