    return (
        <Grid 
            container 
            sx={{
            display: 'flex',
            justifyContent: "space-around"
            }}
        >
            {isLoading && (
                <CircularProgress 
                    size={200}
                    sx={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)'
                    }}
                />
            )}
            {
                !isLoading && loadedMeal && (loadedMeal.map((pick) => (
                    <Grid 
                        xs={12} sm={6} 
                        key={pick.id}
                        sx={{
                        display:'flex',
                        justifyContent:'center'
                        }}
                    >
                        <MyItem pick={pick} />
                    </Grid>
                )))
            }

        </Grid>
    )
}
export default MyMealsPage
