import { useState } from "react";
import {
  Button,
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Modal,
  Typography,
} from "@mui/material";

const RestaurantItem = ({ restaurant }) => {
  const { name, location, image, address, hours } = restaurant;
  const [open, setOpen] = useState(false);
  const clickHandler = () => {
    setOpen(true);
  };
  const clickToCloseHandler = () => {
    setOpen(false);
  };

  return (
    <div className="restaurant-list" >
      <Card className="card" sx={{ maxWidth: 700 }}>
        <div >
          <CardMedia component="img" height="250" image={image} alt={name} />
        </div>
        <CardContent className="card-content">
          <Typography align="left" gutterBottom variant="h5" component="div">
            {name}
          </Typography>
          <Typography
            align="left"
            variant="body1"
            color="text.secondary"
          >
            {address}
          </Typography>
          <Typography
            align="left"
            variant="body1"
            color="text.secondary"
          >
            Hours of Operation: {hours}
          </Typography>

          <Modal
            open={open}
            onClose={clickToCloseHandler}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
          >
            <div className="map-container">
              <button onClick={clickToCloseHandler} className="button">
                X
              </button>
              <iframe
                title="myframe"
                width="700"
                height="500"
                id="gmap_canvas"
                src={location}
              ></iframe>
            </div>
          </Modal>
          <button id="map" onClick={clickHandler}>
            View on Google Map
          </button>
        </CardContent>
        <CardActions className="action-btns">
          <Button id="happy-btn" size="small"></Button>
        </CardActions>
      </Card>
    </div>
  );

  return (
    <Card 
      sx={{ 
        maxWidth: 400,
        width:'85%'
      }}
    >
      <CardMedia
        sx={{width: '100%', height: {xs:'50vw',sm:'20vw'}, mt:3}} 
        image={image} 
        alt={name}
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {name}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {description}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={likeHandler}><InsertEmoticonIcon/>{numberOfLike}</Button>
        <Button size="small"onClick={dislikeHandler}><SentimentVeryDissatisfiedIcon />{numberOfDislike}</Button>
      </CardActions>
    </Card>
  )
};
export default RestaurantItem;
