import React,{useState} from "react";
import { TextField, Button, Box , Typography} from "@mui/material";
import { validateEmail, validatePassword } from './validation';

export default function Login() {
  const [isLoginMode, setIsLoginMode]=useState(true)
  const [showErrorEmail, setShowErrorEmail]=useState(false)
  const [showErrorPassword, setShowErrorPassword]=useState(false)
  const [showErrorName, setShowErrorName]=useState(false)

  if(isLoginMode){
    let form ={
      email:'',
      password:'',
      isValid:false
    }
  }else{
    let form ={
      name:'',
      email:'',
      password:'',
      isValid:false
    }
  }

  const switchModeHandler=()=>{
    setIsLoginMode(prev=>!prev)
  }

  const validateForm=(e)=>{
    e.preventDefault()
    const data = new FormData(e.currentTarget);
    const email = data.get('email');
    const password = data.get('password');
    const name=data.get('password');
    if(!isLoginMode && !name){
      setShowErrorName('Please enter a user name')
    }
    const passwordValidateError = validatePassword(password)
    if (passwordValidateError) {
      setShowErrorPassword(passwordValidateError);
    }
    const emailValidateResult = validateEmail(email)
    if (!emailValidateResult) {
      setShowErrorEmail("Please enter valid email address");
    }
    return (emailValidateResult && !passwordValidateError &&(!isLoginMode && !name))
  }
  const handleSubmit=(e)=>{
    e.preventDefault()
    if(validateForm(e)){

    }
  }

  return (
    <>
        <Box
          sx={{my:8, display: 'flex', flexDirection: 'column', alignItems: 'center'}}
        >
          <Typography component="h1" variant="h5">{isLoginMode?'LOG IN': 'SIGN UP'}</Typography>
          <Box sx={{width:'50%'}}>
            <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
              {!isLoginMode&& (
                <TextField
                  error={showErrorName}
                  helperText={showErrorName}
                  margin="normal"
                  required
                  fullWidth
                  id="name"
                  label="Name"
                  name="name"
                />
              )}
              <TextField
                error={showErrorEmail}
                helperText={showErrorEmail}
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
              />
              <TextField
                error={showErrorPassword}
                helperText={showErrorPassword}
                margin="normal"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
              />
              <Button variant="contained" type="submit" fullWidth sx={{ mt: 3, mb: 2 }}>Submit</Button>

            </Box>
            <Button variant="outlined" fullWidth onClick={switchModeHandler} sx={{ mt: 3, mb: 2 }}>SWITCH TO {isLoginMode?'SIGN UP': 'LOG IN'}</Button>
          </Box>
        </Box>
    </>
  )
}
