import React,{useState} from "react";
import Button from "@mui/material/Button";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import MenuIcon from "@mui/icons-material/Menu";
import { Outlet, Link } from "react-router-dom";

export default function Header() {
  //if window width change to below 400, then navigation should change to menu version
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  console.log(windowWidth)

  // Dropdown menu functionalities
  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <>
      <header class="headerContainer">
        <Link style={{ textDecoration: "none" }} to="/home">
          <h1>Taste of York</h1>
        </Link>

        <div className="header-right">
          <h3>
            <Link style={{textDecoration: 'none', color:'gray'}} to="/login" >Auth</Link>
          </h3>

          <Button
            id="menu-button"
            aria-controls={open ? "menu" : undefined}
            aria-haspopup="true"
            aria-expanded={open ? "true" : undefined}
            onClick={handleClick}
          >
            <MenuIcon style={{ color: "white" }} />
          </Button>
          <Menu
            id="menu"
            anchorEl={anchorEl}
            open={open}
            onClose={handleClose}
            MenuListProps={{
              "aria-labelledby": "menu-button",
            }}
          >
            <Link
              style={{ textDecoration: "none", color: "black" }}
              to="/findRestaurants"
            >
              <MenuItem onClick={handleClose}>Restaurants</MenuItem>
            </Link>
            <Link
              style={{ textDecoration: "none", color: "black" }}
              to="/studentsPicks"
            >
              <MenuItem onClick={handleClose}>Student Picks</MenuItem>
            </Link>
          </Menu>
        </div>
      </header>
      <Outlet />

    </>
  );
}
